import React, { useState, useContext, useEffect } from "react";
import { Button } from "../button/Button";
import TextInput from '../textInput/TextInput'
import M from "materialize-css";
import { ADD_JOB } from "../../types/types";
import { StoreContext } from '../../context/StoreContext';


const AddEditIngredient = ({ ...props}) => {
  const { dispatch } = useContext(StoreContext);

  useEffect(() => {
    M.AutoInit();
    if (props.job) {
        setTodo({
          todo: props.job.todo,
        });
      }
  }, []);

  const [ todo, setTodo ] = useState("");


  const handleChange = (event) => {
      console.log(event.target.value)
    setTodo(event.target.value)
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const newJob = {
        id: new Date().valueOf(),
        dayId: props.id,
        todo: todo
    }

    dispatch({ type: ADD_JOB, payload: newJob });
    setTodo("")
  };

  console.log("propsjob", props)


  return (
    <div>
      <a
        className='btn modal-trigger'
        data-target={props.id}
      >
          {props.todo ? "Edit Job" : "Add Job" }
      </a>
      <div id={props.id} className='modal'> 
       <form onSubmit={handleSubmit}>
          <div className='modal-content'>
            <h4> {props.job ? "Edit Job" : "Add New Job"}</h4> 
            <TextInput
              type='text'
              name='Job to DO'
              className='validate'
              value={todo}
              onChange={handleChange}
              htmlFor='Job to DO'
              label='Job to Do'
            />
            <div className='modal-footer'>
              <Button
                type='submit'
                className="btn teal darken-2 z-depth-2 modal-close"
                text='submit'
              />
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddEditIngredient;
